unit.switch:
switch(day)
            {   
                case SUN:
                case SAT: cout << "easy\n";
                          break;
                case FRI: cout << "moderate\n";
                          break;           
                case MON:
                case TUE:
                case WED:
                case THU: cout << "hard\n";
                            break;
                default: cout << "imaginary\n";
            }
