unit.switch:
switch(month)
    {
        case JAN:
        case FEB:
        case MAR:
        case APR: cout << "Spring semester\n";
                  season = SPRING;      
                  break; 
        case MAY:    
        case JUN:
        case JUL:
        case AUG: cout << "Summer semester\n";
                  season = SUMMER;
                  break;
        case SEP:
        case OCT:                 
        case NOV:
        case DEC: cout << "Fall semester\n";
                  season = FALL;
                  break;
        default: cout << "Invalid month!\n";
                 season = INVALID;
    }
